		end loop;
		close C05;

		open C06;
		loop
		fetch C06 into
			nr_seq_guia_mat_w;
		EXIT WHEN NOT FOUND; /* apply on C06 */
			begin
			open C03;
			loop
			fetch C03 into
				ds_menssagem_w;
			EXIT WHEN NOT FOUND; /* apply on C03 */
				begin
				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w	:= ds_menssagem_w;
				else
					ds_retorno_w	:= ds_retorno_w||chr(13)||ds_menssagem_w;
				end if;
				end;
			end loop;
			close C03;
			end;
		end loop;
		close C06;
		end;
	end loop;
	close C04;

	open C01;
	loop
	fetch C01 into
		ds_menssagem_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ds_menssagem_w;
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||ds_menssagem_w;
		end if;
		end;
	end loop;
	close C01;
elsif (nr_seq_guia_proc_p IS NOT NULL AND nr_seq_guia_proc_p::text <> '') then
	open C02;
	loop
	fetch C02 into
		ds_menssagem_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ds_menssagem_w;
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||ds_menssagem_w;
		end if;
		end;
	end loop;
	close C02;
elsif (nr_seq_guia_mat_p IS NOT NULL AND nr_seq_guia_mat_p::text <> '') then
	open C03;
	loop
	fetch C03 into
		ds_menssagem_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		begin
		if (coalesce(ds_retorno_w::text, '') = '') then
			ds_retorno_w	:= ds_menssagem_w;
		else
			ds_retorno_w	:= ds_retorno_w||chr(13)||ds_menssagem_w;
		end if;
		end;
	end loop;
	close C03;
end if;

return	ds_retorno_w;

