CREATE OR REPLACE FUNCTION pls_obter_proc_mat_prestador (nr_seq_prestador_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_material_p bigint, nr_seq_prest_fornec_p bigint) RETURNS varchar AS $body$
DECLARE

/*
NÃO UTILIZAR MAIS ESTA ROTINA, FOI CRIADA A FUNCTION "PLS_OBTER_SE_PROC_PRESTADOR" QUE FAZ A MESMA VERIFICAÇÃO
*/
ds_retorno_w		varchar(1);
cd_area_w		bigint;
cd_especialidade_w	bigint;
cd_grupo_w		bigint;
nr_seq_estrutura_mat_w	bigint;
qt_proc_mat_w		integer;


BEGIN

begin
select	coalesce(cd_area_procedimento,0),
	coalesce(cd_especialidade,0),
	coalesce(cd_grupo_proc,0)
into STRICT	cd_area_w,
	cd_especialidade_w,
	cd_grupo_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and	ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	cd_area_w		:= 0;
	cd_especialidade_w	:= 0;
	cd_grupo_w		:= 0;
end;

begin
select	coalesce(nr_seq_estrut_mat,0)
into STRICT	nr_seq_estrutura_mat_w
from	pls_material
where	nr_sequencia = nr_seq_material_p;
exception
	when others then
	nr_seq_estrutura_mat_w := 0;
end;

select	count(*)
into STRICT	qt_proc_mat_w
FROM pls_prestador a
LEFT OUTER JOIN pls_prestador_proc c ON (a.nr_sequencia = c.nr_seq_prestador))
, (a
LEFT OUTER JOIN pls_prestador_mat b ON ((a.nr_sequencia = b.nr_seq_prestador)
WHERE a.nr_sequencia = nr_seq_prestador_p   and ((coalesce(b.nr_seq_material, nr_seq_material_p) = nr_seq_material_p
and	coalesce(b.nr_seq_estrutura_mat,nr_seq_estrutura_mat_w) = nr_seq_estrutura_mat_w)
and	coalesce(b.nr_seq_prest_fornec,coalesce(nr_seq_prest_fornec_p,0)) = coalesce(nr_seq_prest_fornec_p,0)
	or (	coalesce(c.cd_procedimento, cd_procedimento_p) = cd_procedimento_p
	and	coalesce(c.cd_area_procedimento, cd_area_w) = cd_area_w
	and	coalesce(c.cd_especialidade_proc, cd_especialidade_w) = cd_especialidade_w
	and	coalesce(c.cd_grupo_proc,cd_grupo_w) = cd_grupo_w));

if (qt_proc_mat_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
