CREATE OR REPLACE FUNCTION pls_obter_produtos_contrato ( nr_seq_contrato_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ds_plano_w		varchar(4000);

C01 CURSOR FOR
	SELECT	a.ds_plano
	from	pls_contrato_plano	c,
		pls_contrato		b,
		pls_plano		a
	where	c.nr_seq_contrato	= b.nr_sequencia
	and	c.nr_seq_plano		= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_contrato_p
	and	c.ie_situacao		= 'A';


BEGIN

open C01;
loop
fetch C01 into
	ds_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w	:= ds_retorno_w || ds_plano_w||', ';

	end;
end loop;
close C01;

return	substr(ds_retorno_w,1,length(ds_retorno_w)-2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
