CREATE OR REPLACE FUNCTION pls_obter_produto_regra_web ( nr_seq_produto_p bigint) RETURNS varchar AS $body$
DECLARE


cd_plano_w		varchar(20);


BEGIN

if (nr_seq_produto_p IS NOT NULL AND nr_seq_produto_p::text <> '') then
	select	max(cd_plano)
	into STRICT	cd_plano_w
	from	pls_plano
	where 	nr_sequencia = nr_seq_produto_p
	and	ie_situacao = 'A'
	and	ie_tipo_operacao = 'T';

end if;

return cd_plano_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
