SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_prod_novo_igual_ant ( nr_seq_segurado_p bigint, nr_seq_contrato_p bigint, ie_rescindi_atual_p text, dt_contratacao_nova_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(10);
qt_registros_w			bigint;
nr_seq_plano_novo_w		bigint;
nr_seq_plano_ant_w		bigint;
ie_regulamentacao_novo_w		varchar(10);
nr_protocolo_ans_novo_w		varchar(20);
ie_regulamentacao_ant_w		varchar(10);
nr_protocolo_ans_ant_w		varchar(20);
dt_rescisao_w			timestamp;


BEGIN
ds_retorno_w	:= 'N';

select	count(*)
into STRICT	qt_registros_w
from	pls_contrato_plano
where	nr_seq_contrato	= nr_seq_contrato_p
and	ie_situacao	= 'A';

if (qt_registros_w = 1) then
	select	nr_seq_plano
	into STRICT	nr_seq_plano_novo_w
	from	pls_contrato_plano
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	ie_situacao	= 'A';

	select	pls_obter_produto_benef(nr_sequencia,dt_contratacao_nova_p),
		dt_rescisao
	into STRICT	nr_seq_plano_ant_w,
		dt_rescisao_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	select	ie_regulamentacao,
		nr_protocolo_ans
	into STRICT	ie_regulamentacao_novo_w,
		nr_protocolo_ans_novo_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_novo_w;

	select	ie_regulamentacao,
		nr_protocolo_ans
	into STRICT	ie_regulamentacao_ant_w,
		nr_protocolo_ans_ant_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_ant_w;

	if (ie_regulamentacao_novo_w = 'P') and (ie_regulamentacao_ant_w = 'P') then
		if (trim(both nr_protocolo_ans_novo_w) = trim(both nr_protocolo_ans_ant_w)) then
			/*aaschlote - Caso o beneficiario esteja ativo e n達o for rescindido, ou a possuir rescis達o futura com ades達o inferior, ent達o consiste,*/

			if	((coalesce(dt_rescisao_w::text, '') = '') and (ie_rescindi_atual_p = 'N') or (dt_rescisao_w IS NOT NULL AND dt_rescisao_w::text <> '') and (dt_rescisao_w	> dt_contratacao_nova_p)) then
				ds_retorno_w	:= 'S';
			end if;
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
