CREATE OR REPLACE FUNCTION pls_obter_proximo_dia_util ( dt_referencia_p timestamp, ie_acao_p text, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS timestamp AS $body$
DECLARE


				
/*
ie_acao_p
(M,A,P)(Manter, Antecipar, Postergar)
*/
qt_dia_w	bigint := 0;
dt_vencimento_w	timestamp := dt_referencia_p;
qt_feriado_w	integer;
			

BEGIN

if (ie_acao_p <> 'M') then
	while(qt_dia_w = 0) loop
		begin
		select	count(1)
		into STRICT	qt_feriado_w
		from	feriado
		where	cd_estabelecimento = cd_estabelecimento_p
		and	dt_feriado	= dt_vencimento_w;

		if (qt_feriado_w = 0) and (pkg_date_utils.is_business_day(dt_vencimento_w) = 1)  then
			qt_dia_w	:= qt_dia_w + 1;
		else
			if (ie_acao_p = 'P') then
				dt_vencimento_w	:= dt_vencimento_w + 1;
			elsif (ie_acao_p = 'A') then
				dt_vencimento_w	:= dt_vencimento_w - 1;
			end if;
		end if;
		end;
	end loop;
end if;

return	dt_vencimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
