CREATE OR REPLACE FUNCTION pls_obter_proximo_reajuste ( nr_seq_contrato_p bigint, dt_base_p timestamp default null) RETURNS timestamp AS $body$
DECLARE


dt_proximo_reajuste_w	timestamp;
dt_reajuste_w		timestamp;
dt_base_w		timestamp;


BEGIN

if (coalesce(dt_base_p::text, '') = '') then
	dt_base_w	:= clock_timestamp();
else
	dt_base_w	:= dt_base_p;
end if;

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	trunc(coalesce(a.dt_reajuste,a.dt_contrato),'mm')
	into STRICT	dt_reajuste_w
	from	pls_contrato a
	where	a.nr_sequencia	= nr_seq_contrato_p;

	if (dt_reajuste_w < dt_base_w) then
		if (trunc(dt_reajuste_w,'yy') = trunc(dt_base_w,'yy')) then
			dt_proximo_reajuste_w	:= add_months(dt_reajuste_w,12);
		else
			dt_proximo_reajuste_w	:= add_months(dt_reajuste_w,pls_obter_meses_entre_datas(trunc(dt_reajuste_w,'yy'),trunc(dt_base_w,'yy')));

			if (dt_proximo_reajuste_w <= dt_base_w) then
				if (trunc(dt_proximo_reajuste_w,'yy') = trunc(dt_base_w,'yy')) then
					dt_proximo_reajuste_w := add_months(dt_proximo_reajuste_w,12);
				elsif (trunc(dt_proximo_reajuste_w,'yy') > trunc(dt_base_w,'yy')) then
					dt_proximo_reajuste_w := add_months(dt_proximo_reajuste_w,-12);
				end if;
			end if;
		end if;
	else
		dt_proximo_reajuste_w := dt_reajuste_w;
	end if;
end if;

return	dt_proximo_reajuste_w;

end;
$body$
