CREATE OR REPLACE FUNCTION pls_obter_prox_colun_agrup_bol ( nr_seq_estrut_agrup_p bigint) RETURNS bigint AS $body$
DECLARE


nr_coluna_w		bigint;


BEGIN

select	max(NR_COLUNA_AGRUP)
into STRICT	nr_coluna_w
from	PLS_ESTRUT_BANDA_BOL_INFO
where	NR_SEQ_ESTRUT_AGRUP	= nr_seq_estrut_agrup_p
and	coalesce(NR_SEQ_COMPOSICAO::text, '') = '';

if (coalesce(nr_coluna_w::text, '') = '') then
	nr_coluna_w	:= 0;
end if;

return	nr_coluna_w+1;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
