SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_prox_fluxo_analise ( nr_seq_auditoria_p bigint, nr_seq_ordem_p bigint) RETURNS bigint AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o próximo fluxo disponível na análise.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_grupo_mesmo_fluxo_w		integer;
nr_seq_ordem_w			bigint;


BEGIN

nr_seq_ordem_w		:= nr_seq_ordem_p;

select 	count(1)
into STRICT	qt_grupo_mesmo_fluxo_w
from	pls_auditoria_grupo
where	nr_seq_ordem		= nr_seq_ordem_w
and	nr_seq_auditoria	= nr_seq_auditoria_p;

while(qt_grupo_mesmo_fluxo_w	> 0) loop
	begin
		nr_seq_ordem_w	:= nr_seq_ordem_w + 1;

		select 	count(1)
		into STRICT	qt_grupo_mesmo_fluxo_w
		from	pls_auditoria_grupo
		where	nr_seq_ordem		= nr_seq_ordem_w
		and	nr_seq_auditoria	= nr_seq_auditoria_p;
	end;
end loop;

return nr_seq_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_prox_fluxo_analise ( nr_seq_auditoria_p bigint, nr_seq_ordem_p bigint) FROM PUBLIC;

