CREATE OR REPLACE FUNCTION pls_obter_pr_reaj_benef ( nr_seq_item_mens_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
nr_seq_reajuste_w		pls_reajuste_preco.nr_sequencia%type;
nr_seq_segurado_preco_w		pls_segurado_preco.nr_sequencia%type;
tx_reajuste_w			pls_segurado_preco.tx_reajuste%type;
nr_seq_reajuste_preco_w		pls_reajuste_preco.nr_sequencia%type;


BEGIN

select	max(a.nr_seq_segurado_preco),
	max(b.nr_seq_reajuste)
into STRICT	nr_seq_segurado_preco_w,
	nr_seq_reajuste_preco_w
from	pls_mensalidade_segurado a,
	pls_mensalidade_seg_item b
where	a.nr_sequencia = b.nr_seq_mensalidade_seg
and	b.nr_sequencia = nr_seq_item_mens_p;

if (nr_seq_reajuste_preco_w IS NOT NULL AND nr_seq_reajuste_preco_w::text <> '') then
	select	max(b.tx_reajuste)
	into STRICT	tx_reajuste_w
	from	pls_reajuste_preco a,
		pls_reajuste b
	where	b.nr_sequencia	= a.nr_seq_reajuste
	and	a.nr_sequencia	= nr_seq_reajuste_preco_w;
elsif (nr_seq_segurado_preco_w IS NOT NULL AND nr_seq_segurado_preco_w::text <> '') then
	select	max(tx_reajuste)
	into STRICT	tx_reajuste_w
	from	pls_segurado_preco
	where	nr_sequencia = nr_seq_segurado_preco_w;

	ds_retorno_w	:= to_char(tx_reajuste_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
