CREATE OR REPLACE FUNCTION pls_obter_qtd_contratos_mes ( dt_mes_referencia_p timestamp, ie_tipo_retorno_p text) RETURNS varchar AS $body$
DECLARE



qt_retorno_w			bigint;
qt_cgc_estipulante_ati_w	bigint;
qt_cgc_subestipulante_ati_w	bigint;
qt_cgc_estipulante_ina_w	bigint;
qt_cgc_subestipulante_ina_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_cgc_estipulante_ati_w
from	pls_contrato	a
where	((trunc(a.dt_rescisao_contrato,'month') > trunc(dt_mes_referencia_p,'Month')) or (coalesce(a.dt_rescisao_contrato::text, '') = ''))
and	trunc(a.dt_contrato,'Month')			<=  trunc(dt_mes_referencia_p,'Month')
and	(a.cd_cgc_estipulante IS NOT NULL AND a.cd_cgc_estipulante::text <> '');

select	count(*)
into STRICT	qt_cgc_estipulante_ina_w
from	pls_contrato	a
where	trunc(a.dt_rescisao_contrato,'month')		<= trunc(dt_mes_referencia_p,'Month')
and	(a.cd_cgc_estipulante IS NOT NULL AND a.cd_cgc_estipulante::text <> '');

select	count(*)
into STRICT	qt_cgc_subestipulante_ati_w
from	pls_sub_estipulante	a,
	pls_contrato		b
where	a.nr_seq_contrato	= b.nr_sequencia
and	((trunc(b.dt_rescisao_contrato,'month') > trunc(dt_mes_referencia_p,'Month')) or (coalesce(b.dt_rescisao_contrato::text, '') = ''))
and	trunc(b.dt_contrato,'Month')		<=  trunc(dt_mes_referencia_p,'Month')
and	(a.cd_cgc IS NOT NULL AND a.cd_cgc::text <> '');

select	count(cd_cgc)
into STRICT	qt_cgc_subestipulante_ina_w
from	pls_sub_estipulante	a,
	pls_contrato		b
where	a.nr_seq_contrato	= b.nr_sequencia
and	trunc(b.dt_rescisao_contrato,'month')		<= trunc(dt_mes_referencia_p,'Month');

if (ie_tipo_retorno_p	= 'A') then
qt_retorno_w	:=	(qt_cgc_estipulante_ati_w) + (qt_cgc_subestipulante_ati_w);

elsif (ie_tipo_retorno_p	= 'I') then
qt_retorno_W	:=	(qt_cgc_estipulante_ina_w) + (qt_cgc_subestipulante_ina_w);

end if;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
