SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qtd_cont_glosas_web ( nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
qtd_glosas_prot_w	bigint := 0;
nr_seq_conta_w		bigint;
nr_seq_proc_w		bigint;
nr_seq_mat_w		bigint;
qt_registros_w		bigint;

C01 CURSOR FOR 
	SELECT	a.nr_sequencia 
	from	pls_conta a, 
			pls_protocolo_conta b 
	where	a.nr_seq_protocolo = b.nr_sequencia 
	and		b.nr_sequencia = nr_seq_protocolo_p;
	

BEGIN 
 
open C01;
loop 
fetch C01 into 
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	pls_conta_glosa_ocorrencia_v 
	where	nr_seq_conta = nr_seq_conta_w 
	and 	coalesce(nr_seq_proc::text, '') = '' 
	and 	coalesce(nr_seq_mat::text, '') = '';
	 
	if (qt_registros_w = 0) then 
	 
		select	count(1) 
		into STRICT	qt_registros_w 
		from	pls_conta_proc a 
		where	a.nr_seq_conta = nr_seq_conta_w 
		and		exists (SELECT	1 
					  from		pls_conta_glosa x 
					  where	x.nr_seq_conta_proc = a.nr_sequencia );
	 
		if (qt_registros_w = 0) then 
		 
			select	count(1) 
			into STRICT	qt_registros_w 
			from	pls_conta_mat a 
			where	a.nr_seq_conta = nr_seq_conta_w 
			and		exists (SELECT	1 
						  from		pls_conta_glosa x 
						  where	x.nr_seq_conta_mat = a.nr_sequencia );
		 
		end if;
		 
	end if;
 
	if (qt_registros_w > 0) then 
		qtd_glosas_prot_w := qtd_glosas_prot_w +1;
	end if;
	 
	end;
end loop;
close C01;			
 
 
return	qtd_glosas_prot_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
