SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qtd_glosas_prot ( nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE


qtd_glosas_conta_w	bigint := 0;
nr_seq_conta_w		bigint;
nr_seq_procedimento_w	bigint;
nr_seq_material_w	bigint;
qt_conta_mat_w		bigint;
qt_conta_proc_w		bigint;
qt_conta_ocorrencia_w	bigint;
qt_conta_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_conta a,
		pls_protocolo_conta b
	where	nr_seq_protocolo = nr_seq_protocolo_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_conta_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_conta_w
	from	tiss_motivo_glosa	b,
		pls_conta_glosa		a
	where	a.nr_seq_motivo_glosa	= b.nr_sequencia
	and	a.nr_seq_conta		= nr_seq_conta_w;

	select	count(*)
	into STRICT	qt_conta_ocorrencia_w
	from	pls_ocorrencia		b,
		pls_ocorrencia_benef 	a
	where	a.nr_seq_ocorrencia	= b.nr_sequencia
	and	a.ie_auditoria		= 'S'
	and	a.nr_seq_conta		= nr_seq_conta_w
	and	coalesce(a.nr_seq_proc::text, '') = ''
	and	coalesce(a.nr_seq_mat::text, '') = '';

	qtd_glosas_conta_w := qt_conta_w + qt_conta_ocorrencia_w;

	if (qtd_glosas_conta_w = 0) then
		select	count(*)
		into STRICT	qt_conta_proc_w
		from	pls_conta_glosa		a,
			tiss_motivo_glosa	b
		where	a.nr_seq_motivo_glosa	= b.nr_sequencia
		and	a.nr_seq_conta_proc	in (	SELECT	nr_sequencia
							from	pls_conta_proc
							where	nr_seq_conta	= nr_seq_conta_w);

		select	count(*)
		into STRICT	qt_conta_ocorrencia_w
		from	pls_ocorrencia		b,
			pls_ocorrencia_benef 	a
		where	a.nr_seq_ocorrencia	= b.nr_sequencia
		and	a.ie_auditoria		= 'S'
		and	a.nr_seq_conta		= nr_seq_conta_w
		and	a.nr_seq_proc		in (	SELECT	nr_sequencia
							from	pls_conta_proc
							where	nr_seq_conta	= nr_seq_conta_w);

		qtd_glosas_conta_w := qt_conta_proc_w + qt_conta_ocorrencia_w;

		if (qtd_glosas_conta_w = 0) then
			select	count(*)
			into STRICT	qt_conta_mat_w
			from	pls_conta_glosa		a,
				tiss_motivo_glosa	b
			where	a.nr_seq_motivo_glosa	= b.nr_sequencia
			and	a.nr_seq_conta_mat	in (	SELECT	nr_sequencia
								from	pls_conta_mat
								where	nr_seq_conta	= nr_seq_conta_w);

			select	count(*)
			into STRICT	qt_conta_ocorrencia_w
			from	pls_ocorrencia		b,
				pls_ocorrencia_benef 	a
			where	a.nr_seq_ocorrencia	= b.nr_sequencia
			and	a.ie_auditoria		= 'S'
			and	a.nr_seq_conta		= nr_seq_conta_w
			and	a.nr_seq_mat		in (	SELECT	nr_sequencia
								from	pls_conta_mat
								where	nr_seq_conta	= nr_seq_conta_w);

			qtd_glosas_conta_w := qt_conta_mat_w + qt_conta_ocorrencia_w;
		end if;
	end if;
	if (qtd_glosas_conta_w > 0) then
		goto final;
	end if;
	end;
end loop;
close C01;

<<final>>

qt_conta_ocorrencia_w := 0;

return	qtd_glosas_conta_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
