SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qtd_vidas_contrato ( nr_seq_contrato_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


/* ie_tipo_p
	A - Ativos
	T - Todos
*/
vl_retorno_w			bigint;


BEGIN

if (ie_tipo_p	= 'A') then
	select	count(*)
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and (coalesce(dt_rescisao::text, '') = '' or ((dt_rescisao IS NOT NULL AND dt_rescisao::text <> '') and coalesce(dt_reativacao,dt_rescisao) > dt_rescisao));
elsif (ie_tipo_p	= 'T') then
	select	count(*)
	into STRICT	vl_retorno_w
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
