CREATE OR REPLACE FUNCTION pls_obter_qtd_vidas_relac_cont ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, ie_tipo_relacionamento_p pls_grupo_contrato.ie_tipo_relacionamento%type) RETURNS varchar AS $body$
DECLARE


qt_vidas_grupo_w	varchar(255);
nr_seq_grupo_w		pls_grupo_contrato.nr_sequencia%type;	
	

BEGIN

select	max(b.nr_sequencia)
into STRICT	nr_seq_grupo_w
from	pls_contrato_grupo a,
	pls_grupo_contrato b
where	b.nr_sequencia		 = a.nr_seq_grupo
and	a.nr_seq_contrato	 = nr_seq_contrato_p
and	b.ie_tipo_relacionamento = ie_tipo_relacionamento_p;

if (nr_seq_grupo_w IS NOT NULL AND nr_seq_grupo_w::text <> '') then
	qt_vidas_grupo_w	:= pls_obter_qt_vidas_grupo_relac(nr_seq_grupo_w, clock_timestamp());
else
	qt_vidas_grupo_w	:= null;
end if;

return	qt_vidas_grupo_w;

end;
$body$
