CREATE OR REPLACE FUNCTION pls_obter_qt_aceitos (nr_seq_intercambio_p bigint) RETURNS bigint AS $body$
DECLARE


qt_aceitos_w bigint;


BEGIN
if (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	begin
	select	count(*)
	into STRICT	qt_aceitos_w
	from	ptu_intercambio_benef	b,
		ptu_intercambio_empresa	a
	where	b.nr_seq_empresa        = a.nr_sequencia
	and	a.nr_seq_intercambio    = nr_seq_intercambio_p
	and	b.ie_status             in ('A','C');
	end;
end if;

return	qt_aceitos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
