CREATE OR REPLACE FUNCTION pls_obter_qt_benef_a100 ( nr_seq_lote_receb_p bigint, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE

/*ie_opcao_p: 

'P'= Pr√≥prio
'R' = Recebido
*/
	
qt_registros_w	bigint;
			

BEGIN

select	count(1)
into STRICT 	qt_registros_w
from	ptu_intercambio_benef_simp a,
	ptu_intercambio_lote_receb b
where	a.ie_proprio_recebido = ie_tipo_p
and	a.nr_seq_lote_receb = b.nr_sequencia
and 	b.nr_sequencia = nr_seq_lote_receb_p;

return	coalesce(qt_registros_w,0);

end;
$body$
