SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_caracter_conteudo (ds_conteudo_p text, ds_caracter_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		varchar(255);
nr_caracter_w		bigint;
qt_tamanho_w		bigint;
ds_conteudo_w		varchar(4000);


BEGIN
ds_conteudo_w	:= ds_conteudo_p;
qt_retorno_w	:= 0;

while(ds_conteudo_w IS NOT NULL AND ds_conteudo_w::text <> '') loop
	begin

	nr_caracter_w	:= position(ds_caracter_p in ds_conteudo_w);

	if (nr_caracter_w > 0) then
		qt_tamanho_w	:= length(ds_conteudo_w);
		qt_retorno_w	:= qt_retorno_w + 1;
		ds_conteudo_w	:= substr(ds_conteudo_w,nr_caracter_w+1,qt_tamanho_w);
	else
		ds_conteudo_w	:= '';
	end if;

	end;
end loop;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
