SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_contas_analise ( nr_seq_analise_p bigint, ie_tipo_guia_p text) RETURNS bigint AS $body$
DECLARE

 
nr_retorno_w			bigint;
qt_discussao_w			bigint;
qt_pos_w			bigint;
				

BEGIN 
 
select	count(*) 
into STRICT	qt_discussao_w 
from	pls_contestacao_discussao a 
where	a.nr_seq_analise	= nr_seq_analise_p;
 
select	count(1) 
into STRICT	qt_pos_w 
from	pls_conta_pos_estabelecido a 
where	a.nr_seq_analise	= nr_seq_analise_p 
and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''));
 
if (qt_discussao_w > 0) then 
	select	count(count(nr_seq_conta)) 
	into STRICT	nr_retorno_w 
	from	w_pls_discussao_item 
	where	nr_seq_analise = nr_seq_analise_p 
	and	ie_tipo_guia = ie_tipo_guia_p 
	group by nr_seq_conta;
elsif (qt_pos_w > 0) then 
	select	count(distinct b.nr_sequencia) 
	into STRICT	nr_retorno_w 
	from	pls_conta b, 
		pls_conta_pos_estabelecido a 
	where	a.nr_seq_conta	= b.nr_sequencia 
	and	a.nr_seq_analise = nr_seq_analise_p 
	and	b.ie_tipo_guia = ie_tipo_guia_p 
	and	((a.ie_situacao	= 'A') or (coalesce(a.ie_situacao::text, '') = ''));
else 
	select	count(count(nr_sequencia)) 
	into STRICT	nr_retorno_w 
	from	pls_conta 
	where	nr_seq_analise = nr_seq_analise_p 
	and	ie_tipo_guia = ie_tipo_guia_p 
	group by nr_sequencia;
end if;
 
return	nr_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
