CREATE OR REPLACE FUNCTION pls_obter_qt_contas_pos ( nr_seq_analise_p bigint, ie_tipo_guia_p text) RETURNS bigint AS $body$
DECLARE

 
nr_retorno_w			bigint;
				

BEGIN 
 
select	count(1) 
into STRICT	nr_retorno_w 
from	( 
	SELECT	a.nr_sequencia 
	from	pls_conta a, 
		pls_conta_pos_estabelecido b 
	where	a.nr_sequencia	 = b.nr_seq_conta 
	and	b.nr_seq_analise = nr_seq_analise_p 
	and	a.ie_tipo_guia	 = ie_tipo_guia_p 
	and	((b.ie_situacao	= 'A') or (coalesce(b.ie_situacao::text, '') = '')) 
	group by a.nr_sequencia 
	) alias5;
 
return	nr_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
