CREATE OR REPLACE FUNCTION pls_obter_qt_cti_uti_milliman ( nr_seq_conta_proc_p bigint) RETURNS bigint AS $body$
DECLARE

qt_retorno_w			bigint	:= 0;
cd_procedimento_w		bigint;
cd_especialidade_w		bigint;


BEGIN

select	max(cd_procedimento)
into STRICT	cd_procedimento_w
from	pls_conta_proc
where	nr_sequencia	 = nr_seq_conta_proc_p
and	ie_origem_proced = 1;

if (coalesce(cd_procedimento_w,0) in (90070001,90070002,90070003,90070004,90070005,90070006,90070007,90070008,90070009)) then
	select	sum(qt_procedimento)
	into STRICT	qt_retorno_w
	from	pls_conta_proc
	where	nr_sequencia	 = nr_seq_conta_proc_p
	and	ie_origem_proced = 1
	and	cd_procedimento	 = cd_procedimento_w;
else
	begin
	select	(obter_dados_estrut_proc(cd_procedimento,ie_origem_proced,'C','E'))::numeric
	into STRICT	cd_especialidade_w
	from	procedimento
	where	cd_procedimento	= cd_procedimento_w
	and	ie_origem_proced	= 1;
	exception
	when others then
		cd_especialidade_w	:= 0;
	end;
	if (cd_especialidade_w	= 90000000) then
		select	sum(qt_procedimento)
		into STRICT	qt_retorno_w
		from	pls_conta_proc
		where	nr_sequencia	 = nr_seq_conta_proc_p
		and	ie_origem_proced = 1
		and	cd_procedimento	 = cd_procedimento_w;
	end if;
end if;

return	qt_retorno_w;

end;
