CREATE OR REPLACE FUNCTION pls_obter_qt_desp_faixa_etaria ( qt_idade_inicial_p bigint, qt_idade_final_p bigint, ie_tipo_data_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_tipo_conta_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_conta_w			bigint;
nr_seq_segurado_w		bigint;
vl_total_liberado_w		double precision;
vl_despesas_w			double precision;
cd_pessoa_fisica_w		varchar(10);
qt_idade_benef_w		bigint;
dt_conta_w			timestamp;

C01 CURSOR FOR
	SELECT	c.nr_sequencia,
		a.cd_pessoa_fisica,
		c.dt_atendimento_referencia,
		c.vl_total
	from	pls_segurado		a,
		pls_protocolo_conta	b,
		pls_conta		c
	where	a.nr_sequencia = c.nr_seq_segurado
	and	b.nr_sequencia = c.nr_seq_protocolo
	and	b.ie_tipo_protocolo	= 'C'
	and	((ie_tipo_data_p = 'P' and b.dt_protocolo between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'M' and b.dt_mes_competencia between dt_inicial_p and fim_dia(dt_final_p)) or (ie_tipo_data_p = 'N'))
	and	coalesce(c.ie_tipo_conta, 'O') = ie_tipo_conta_p;


BEGIN
vl_despesas_w	:= 0;

open C01;
loop
fetch C01 into
	nr_seq_conta_w,
	cd_pessoa_fisica_w,
	dt_conta_w,
	vl_total_liberado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_idade_benef_w	:= obter_idade_pf(cd_pessoa_fisica_w,dt_conta_w,'A');

	if (qt_idade_benef_w between qt_idade_inicial_p and qt_idade_final_p) then
		if (coalesce(vl_total_liberado_w,0) <> 0) then
			vl_despesas_w	:= coalesce(vl_despesas_w,0) + coalesce(vl_total_liberado_w,0);
		end if;
	end if;

	end;
end loop;
close C01;

return	vl_despesas_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
