CREATE OR REPLACE FUNCTION pls_obter_qt_faixa_segurado ( nr_seq_plano_preco_p bigint) RETURNS bigint AS $body$
DECLARE


qt_segurado_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_segurado_w
from	pls_plano		a,
	pls_tabela_preco 	b,
	pls_plano_preco 	c,
	pls_segurado		d,
	pessoa_fisica		e
where	b.nr_seq_plano		= a.nr_sequencia
and	d.nr_seq_plano		= a.nr_sequencia
and	c.nr_Seq_tabela		= b.nr_sequencia
and	d.nr_seq_tabela		= b.nr_sequencia
and	d.cd_pessoa_fisica	= e.cd_pessoa_fisica
and	c.nr_sequencia	= nr_seq_plano_preco_p
and	obter_idade_pf(d.cd_pessoa_fisica,clock_timestamp(),'A') between c.qt_idade_inicial and c.qt_idade_final
group by c.qt_idade_inicial;

return	qt_segurado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
