CREATE OR REPLACE FUNCTION pls_obter_qt_familia_benef ( nr_seq_segurado_p bigint, nr_seq_titular_p bigint, ie_opcao_p text, dt_verificar_ativo_p timestamp) RETURNS bigint AS $body$
DECLARE


/*
ie_opcao_p
L - Liberado
N - NÃ£o liberado
*/
qt_registros_w		bigint;


BEGIN

if (coalesce(nr_seq_titular_p::text, '') = '') then
	if (ie_opcao_p = 'L') then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_segurado
		where	nr_seq_titular	= nr_seq_segurado_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > dt_verificar_ativo_p));
	elsif (ie_opcao_p = 'N') then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_segurado
		where	nr_seq_titular	= nr_seq_segurado_p;
	end if;
	qt_registros_w	:= coalesce(qt_registros_w,0) + 1;

elsif (nr_seq_titular_p IS NOT NULL AND nr_seq_titular_p::text <> '') then
	if (ie_opcao_p = 'L') then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_segurado
		where	nr_seq_titular	= nr_seq_titular_p
		and	nr_sequencia	<> nr_seq_segurado_p
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > dt_verificar_ativo_p));
	elsif (ie_opcao_p = 'N') then
		select	count(1)
		into STRICT	qt_registros_w
		from	pls_segurado
		where	nr_seq_titular	= nr_seq_titular_p
		and	nr_sequencia	<> nr_seq_segurado_p;
	end if;
	qt_registros_w	:= coalesce(qt_registros_w,0) + 2;
end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
