CREATE OR REPLACE FUNCTION pls_obter_qt_familia_proposta ( nr_seq_proposta_benef_p bigint, nr_seq_titular_p bigint) RETURNS bigint AS $body$
DECLARE




qt_registros_w		bigint;


BEGIN

if (coalesce(nr_seq_titular_p::text, '') = '') then

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_proposta_beneficiario
	where	nr_seq_titular	= nr_seq_proposta_benef_p
	and	coalesce(dt_cancelamento::text, '') = '';

	qt_registros_w	:= coalesce(qt_registros_w,0) + 1;

elsif (nr_seq_titular_p IS NOT NULL AND nr_seq_titular_p::text <> '') then

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_proposta_beneficiario
	where	nr_seq_titular	= nr_seq_titular_p
	and	nr_sequencia	<> nr_seq_proposta_benef_p
	and	coalesce(dt_cancelamento::text, '') = '';

	qt_registros_w	:= coalesce(qt_registros_w,0) + 2;
end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
