CREATE OR REPLACE FUNCTION pls_obter_qt_fatura_ptu_camara ( nr_seq_camara_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
IE_OPCAO_P
1 = Total de unimeds credoras
2 = Total de unimeds devedora
3 = Total de faturas
4 = Valor total das faturas
*/
retorno_w		double precision;


BEGIN

if (ie_opcao_p = 	'1') then
	select	count(*)
	into STRICT	retorno_w
	from	ptu_camara_fatura
	where	(cd_unimed_credora IS NOT NULL AND cd_unimed_credora::text <> '')
	and	nr_seq_camara	= nr_seq_camara_p;
elsif (ie_opcao_p = 	'2') then
	select	count(*)
	into STRICT	retorno_w
	from	ptu_camara_fatura
	where	(cd_unimed_devedora IS NOT NULL AND cd_unimed_devedora::text <> '')
	and	nr_seq_camara	= nr_seq_camara_p;
elsif (ie_opcao_p = 	'3') then
	select	count(*)
	into STRICT	retorno_w
	from	ptu_camara_fatura
	where	(nr_fatura IS NOT NULL AND nr_fatura::text <> '')
	and	nr_seq_camara	= nr_seq_camara_p;
elsif (ie_opcao_p = 	'4') then
	select	sum(vl_total_fatura)
	into STRICT	retorno_w
	from	ptu_camara_fatura
	where	nr_seq_camara	= nr_seq_camara_p;
end if;


return	retorno_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
