CREATE OR REPLACE FUNCTION pls_obter_qt_fornec_opm ( nr_seq_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_fornecedores_w	bigint;

/*Essa rotina é chamada no código fonte*/

BEGIN


select	count(1)
into STRICT	qt_fornecedores_w
from	pls_mat_uni_fed_sc_preco c,
	pls_mat_unimed_fed_sc b,
	pls_material a,
	pls_fornec_mat_fed_sc	d
where	b.nr_sequencia = c.nr_seq_mat_unimed
and	a.cd_material_ops = b.cd_material
and	d.cd_fornecedor = c.cd_fornecedor_fed_sc
and	d.ie_situacao = 'A'
and	a.nr_sequencia = nr_seq_material_p;


return	qt_fornecedores_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
