CREATE OR REPLACE FUNCTION pls_obter_qt_glosa_item ( cd_procedimento_p pls_conta_proc.cd_procedimento%type, ie_origem_proced_p pls_conta_proc.ie_origem_proced%type, nr_seq_material_p pls_conta_mat.nr_seq_material%type, nr_seq_guia_p pls_conta.nr_sequencia%type, cd_motivo_tiss_p tiss_motivo_glosa.cd_motivo_tiss%type) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: 	Buscar a quantidade de vezes que a glosa foi lançada para o item
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
Alterações:
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_motivo_glosa_w	tiss_motivo_glosa.nr_sequencia%type;
qt_glosa_w		integer;


BEGIN

select	max(nr_sequencia)
into STRICT	nr_seq_motivo_glosa_w
from	tiss_motivo_glosa
where	cd_motivo_tiss = cd_motivo_tiss_p;

if (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '')	then

	select	count(a.nr_sequencia)
	into STRICT	qt_glosa_w
	from	pls_conta_proc	a,
		pls_conta	b,
		pls_conta_glosa	c
	where	a.nr_seq_conta		= b.nr_sequencia
	and	a.nr_sequencia		= c.nr_seq_conta_mat
	and	a.cd_procedimento	= cd_procedimento_p
	and	a.ie_origem_proced	= ie_origem_proced_p
	and	b.nr_seq_guia 		= nr_seq_guia_p
	and	c.nr_seq_motivo_glosa   = nr_seq_motivo_glosa_w;

elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')	then

	select	count(a.nr_sequencia)
	into STRICT	qt_glosa_w
	from	pls_conta_mat	a,
		pls_conta	b,
		pls_conta_glosa	c
	where	a.nr_seq_conta		= b.nr_sequencia
	and	a.nr_sequencia		= c.nr_seq_conta_mat
	and	a.nr_seq_material 	= nr_seq_material_p
	and	b.nr_seq_guia 		= nr_seq_guia_p
	and	c.nr_seq_motivo_glosa   = nr_seq_motivo_glosa_w;

end if;


return	qt_glosa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
