CREATE OR REPLACE FUNCTION pls_obter_qt_inconsisit_item ( nr_seq_procedimento_p bigint, nr_seq_material_p bigint, cd_transacao_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w			varchar(10);


BEGIN

if (nr_seq_procedimento_p	<> 0) then
	select	count(*)
	into STRICT	qt_registros_w
	from	ptu_intercambio_consist
	where	nr_seq_procedimento	= nr_seq_procedimento_p
	and	cd_transacao		= cd_transacao_p;
elsif (nr_seq_material_p	<> 0) then
	select	count(*)
	into STRICT	qt_registros_w
	from	ptu_intercambio_consist
	where	nr_seq_material		= nr_seq_material_p
	and	cd_transacao		= cd_transacao_p;
end if;

return	qt_registros_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
