CREATE OR REPLACE FUNCTION pls_obter_qt_inconsit_pessoa ( nr_seq_lote_p bigint, cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS bigint AS $body$
DECLARE


qt_inconsistencias_w	bigint	:= null;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then

	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		select	count(*)
		into STRICT	qt_inconsistencias_w
		from	pls_pessoa_inconsistente a
		where	a.nr_seq_lote		= nr_seq_lote_p
		and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	coalesce(a.dt_consistente::text, '') = '';
	else
		select	count(*)
		into STRICT	qt_inconsistencias_w
		from	pls_pessoa_inconsistente a
		where	a.nr_seq_lote	= nr_seq_lote_p
		and	a.cd_cgc	= cd_cgc_p
		and	coalesce(a.dt_consistente::text, '') = '';
	end if;
end if;

return qt_inconsistencias_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
