CREATE OR REPLACE FUNCTION pls_obter_qt_itens_protocolo ( nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE



qt_itens_w	bigint;


BEGIN

select 	count(nr_seq) qtde
into STRICT	qt_itens_w
from (
SELECT 	a.nr_sequencia nr_seq

from 	pls_conta_mat a,
	pls_conta b,
	pls_protocolo_conta c
where	a.nr_seq_conta = b.nr_sequencia
and	b.nr_seq_protocolo = c.nr_sequencia
and	c.nr_sequencia = nr_seq_protocolo_p
and	a.ie_status <> 'D'
and	b.ie_status <> 'C'


union


SELECT 	a.nr_sequencia nr_seq

from 	pls_conta_proc a,
	pls_conta b,
	pls_protocolo_conta c
where	a.nr_seq_conta = b.nr_sequencia
and	b.nr_seq_protocolo = c.nr_sequencia
and	c.nr_sequencia = nr_seq_protocolo_p
and	a.ie_status <> 'D'
and	b.ie_status <> 'C') alias1;

return	qt_itens_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
