CREATE OR REPLACE FUNCTION pls_obter_qt_itens_req_web ( nr_seq_requisicao_p bigint) RETURNS bigint AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a quantidade de itens na requisição para autorização
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/qt_material_w		bigint;
qt_proced_w		bigint;
nr_retorno_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_material_w
from 	pls_requisicao_mat
where 	nr_seq_requisicao = nr_seq_requisicao_p;

select	count(*)
into STRICT	qt_proced_w
from	pls_requisicao_proc
where 	nr_seq_requisicao = nr_seq_requisicao_p;


nr_retorno_w := qt_proced_w + qt_material_w;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
