SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_lib_rec_glosa ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a quantidade liberada para coparticipação do recurso de glosa
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  X]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	double precision;
qt_liberada_w	double precision;
vl_acatado_w	double precision;


BEGIN
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
	select	coalesce(max(qt_procedimento),0)
	into STRICT	qt_liberada_w
	from	pls_conta_proc
	where	nr_sequencia	= nr_seq_conta_proc_p;

	if (qt_liberada_w = 0) then
		select	sum(coalesce(vl_acatado,0))
		into STRICT	vl_acatado_w
		from	pls_rec_glosa_proc
		where	nr_seq_conta_proc = nr_seq_conta_proc_p;

		if (vl_acatado_w > 0) then
			select	coalesce(max(qt_procedimento_imp),0)
			into STRICT	qt_liberada_w
			from	pls_conta_proc
			where	nr_sequencia	= nr_seq_conta_proc_p;
		end if;
	end if;
	ds_retorno_w := coalesce(qt_liberada_w,0);

elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then
	select	coalesce(max(qt_material),0)
	into STRICT	qt_liberada_w
	from	pls_conta_mat
	where	nr_sequencia	= nr_seq_conta_mat_p;

	if (qt_liberada_w = 0) then
		select	sum(coalesce(vl_acatado,0))
		into STRICT	vl_acatado_w
		from	pls_rec_glosa_mat
		where	nr_seq_conta_mat = nr_seq_conta_mat_p;

		if (vl_acatado_w > 0) then
			select	coalesce(max(qt_material_imp),0)
			into STRICT	qt_liberada_w
			from	pls_conta_mat
			where	nr_sequencia	= nr_seq_conta_mat_p;
		end if;
	end if;
	ds_retorno_w := coalesce(qt_liberada_w,0);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_lib_rec_glosa ( nr_seq_conta_proc_p bigint, nr_seq_conta_mat_p bigint) FROM PUBLIC;

