CREATE OR REPLACE FUNCTION pls_obter_qt_meses_pendentes ( cd_pessoa_fisica_p text, cd_cgc_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint	:= 0;
dt_referencia_w			timestamp;


BEGIN

select	min(dt_pagamento_previsto)
into STRICT	dt_referencia_w
from	titulo_receber
where	((cd_pessoa_fisica = cd_pessoa_fisica_p) or (cd_cgc = cd_cgc_p))
and	dt_pagamento_previsto < clock_timestamp()
and	vl_saldo_titulo > 0
and	ie_situacao = '1';

select	pls_obter_meses_entre_datas(dt_referencia_w,clock_timestamp())
into STRICT	qt_retorno_w
;

return qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
