CREATE OR REPLACE FUNCTION pls_obter_qt_ocorr_prot ( nr_seq_protocolo_p pls_protocolo_conta.nr_sequencia%type, nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_mat_p pls_conta_mat.nr_sequencia%type, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
OPÇÕES:
1 - Retorna a quantidade de ocorrências das contas do protocolo
2 - Retorna a quantidade de ocorrências dos procedimentos do protocolo
3 - Retorna a quantidade de ocorrências dos materiais do protocolo
4 - Retorna a soma das quantidades das ocorrências das Contas, Procedimentos e Materiais do protocolo
5 - Retorna a quantidade de ocorrências da conta.
6 - Retorna a quantidade de ocorrências do procedimento
7 - Retorna a quantidade de ocorrências do material
8 - Retorna a quantidade de ocorrências dos procedimentos da conta
9 - Retorna a quantidade de ocorrências dos materiais da conta
*/
qt_ocorrencias_w	bigint;


BEGIN

qt_ocorrencias_w	:= 0;

if (ie_opcao_p = '1') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_conta_imp a,
		pls_ocorrencia_imp b,
		pls_ocorrencia c
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_seq_conta
	and	b.nr_seq_ocorrencia	= c.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '2') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_conta_imp a,
		pls_conta_proc_imp b,
		pls_ocorrencia_imp c,
		pls_ocorrencia d
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= c.nr_seq_conta_proc
	and	c.nr_seq_ocorrencia	= d.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '3') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_conta_imp a,
		pls_conta_mat_imp b,
		pls_ocorrencia_imp c,
		pls_ocorrencia d
	where	a.nr_seq_protocolo	= nr_seq_protocolo_p
	and	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= c.nr_seq_conta_mat
	and	c.nr_seq_ocorrencia	= d.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '4') then
	select	sum(qt_total)
	into STRICT	qt_ocorrencias_w
	from (SELECT	count(1) qt_total
		from	pls_conta_imp a,
			pls_ocorrencia_imp b,
			pls_ocorrencia c
		where	a.nr_seq_protocolo	= nr_seq_protocolo_p
		and	a.nr_sequencia		= b.nr_seq_conta
		and	b.nr_seq_ocorrencia	= c.nr_sequencia
		and	c.ie_situacao 		= 'A'
		
union	all

		PERFORM	count(1) qt_total
		from	pls_conta_imp a,
			pls_conta_proc_imp b,
			pls_ocorrencia_imp c,
			pls_ocorrencia d
		where	a.nr_seq_protocolo	= nr_seq_protocolo_p
		and	a.nr_sequencia		= b.nr_seq_conta
		and	a.nr_sequencia		= c.nr_seq_conta
		and	b.nr_sequencia		= c.nr_seq_conta_proc
		and	c.nr_seq_ocorrencia	= d.nr_sequencia
		and	c.ie_situacao 		= 'A'
		
union	all

		select	count(1) qt_total
		from	pls_conta_imp a,
			pls_conta_mat_imp b,
			pls_ocorrencia_imp c,
			pls_ocorrencia d
		where	a.nr_seq_protocolo	= nr_seq_protocolo_p
		and	a.nr_sequencia		= b.nr_seq_conta
		and	a.nr_sequencia		= c.nr_seq_conta
		and	b.nr_sequencia		= c.nr_seq_conta_mat
		and	c.nr_seq_ocorrencia	= d.nr_sequencia
		and	c.ie_situacao 		= 'A') alias5;
elsif (ie_opcao_p = '5') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_ocorrencia_imp b,
		pls_ocorrencia c
	where	b.nr_seq_conta		= nr_seq_conta_p
	and	b.nr_seq_ocorrencia	= c.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '6') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_ocorrencia_imp b,
		pls_ocorrencia c
	where	b.nr_seq_conta_proc	= nr_seq_proc_p
	and	b.nr_seq_ocorrencia	= c.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '7') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_ocorrencia_imp b,
		pls_ocorrencia c
	where	b.nr_seq_conta_proc	= nr_seq_mat_p
	and	b.nr_seq_ocorrencia	= c.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '8') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_conta_imp a,
		pls_conta_proc_imp b,
		pls_ocorrencia_imp c,
		pls_ocorrencia d
	where	a.nr_sequencia		= nr_seq_conta_p
	and	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= c.nr_seq_conta_proc
	and	c.nr_seq_ocorrencia	= d.nr_sequencia
	and	c.ie_situacao 		= 'A';
elsif (ie_opcao_p = '9') then
	select	count(1)
	into STRICT	qt_ocorrencias_w
	from	pls_conta_imp a,
		pls_conta_mat_imp b,
		pls_ocorrencia_imp c,
		pls_ocorrencia d
	where	a.nr_sequencia		= nr_seq_conta_p
	and	a.nr_sequencia		= b.nr_seq_conta
	and	a.nr_sequencia		= c.nr_seq_conta
	and	b.nr_sequencia		= c.nr_seq_conta_mat
	and	c.nr_seq_ocorrencia	= d.nr_sequencia
	and	c.ie_situacao 		= 'A';
end if;

return	qt_ocorrencias_w;

end;
$body$
