CREATE OR REPLACE FUNCTION pls_obter_qt_parcelas_agravo ( nr_seq_mensalidade_item_p bigint) RETURNS bigint AS $body$
DECLARE


qt_parcelas_w		bigint;


BEGIN

begin
select	max(b.qt_parcelas)
into STRICT	qt_parcelas_w
from	pls_segurado_agravo_parc	a,
	pls_segurado_agravo		b
where	a.nr_seq_segurado_agravo	= b.nr_sequencia
and	a.nr_seq_mensalidade_item	= nr_seq_mensalidade_item_p;
exception
when others then
	qt_parcelas_w	:= null;
end;

return	qt_parcelas_w;

end;
$body$
LANGUAGE PLPGSQL
