CREATE OR REPLACE FUNCTION pls_obter_qt_pendente_atend ( nr_seq_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_retorno_w			integer;


BEGIN

select	count(*)
into STRICT	nr_retorno_w
from	pls_atendimento_evento
where	nr_seq_atendimento	= nr_seq_atendimento_p
and	ie_status	= 'P';

return	nr_retorno_w;

end;
