SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_pessoa_inc_lote (nr_seq_lote_p bigint) RETURNS bigint AS $body$
DECLARE


qt_pessoa_w		bigint;
qt_pf_w			bigint;
qt_pj_w			bigint;


BEGIN

if (nr_seq_lote_p IS NOT NULL AND nr_seq_lote_p::text <> '') then
	select	count(distinct cd_pessoa_fisica)
	into STRICT	qt_pf_w
	from	pls_pessoa_inconsistente
	where	nr_seq_lote	= nr_seq_lote_p
	and	coalesce(dt_consistente::text, '') = ''
	and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '');

	select	count(distinct cd_cgc)
	into STRICT	qt_pj_w
	from	pls_pessoa_inconsistente
	where	nr_seq_lote	= nr_seq_lote_p
	and	coalesce(dt_consistente::text, '') = ''
	and	(cd_cgc IS NOT NULL AND cd_cgc::text <> '');

	qt_pessoa_w	:= qt_pf_w + qt_pj_w;
end if;

return qt_pessoa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_pessoa_inc_lote (nr_seq_lote_p bigint) FROM PUBLIC;

