SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_proc_analise ( nr_seq_analise_p bigint, nr_seq_grupo_serv_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w			pls_conta_proc.qt_procedimento_imp%type := 0;


BEGIN

if (pls_util_cta_pck.usar_novo_agrup = 'S') then

	select	sum(qt_procedimento_imp)
	into STRICT	ds_retorno_w
	from	pls_conta_proc_v
	where	nr_seq_analise	= nr_seq_analise_p
	and	ie_status	!= 'D'
	and	exists (SELECT	1
			 from	pls_grupo_servico_tm grupo
			 where	grupo.nr_seq_grupo_servico = nr_seq_grupo_serv_p
			 and	grupo.ie_origem_proced = ie_origem_proced
			 and	grupo.cd_procedimento = cd_procedimento);
else
	select	sum(qt_procedimento_imp)
	into STRICT	ds_retorno_w
	from	pls_conta_proc_v
	where	nr_seq_analise	= nr_seq_analise_p
	and	ie_status	!= 'D'
	and	exists (SELECT	1
		from	table(pls_grupos_pck.obter_procs_grupo_servico(nr_seq_grupo_serv_p, ie_origem_proced,cd_procedimento)) grupo);
end if;

return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
