SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_proc_mat_conta (nr_seq_prestador_p bigint, ie_opcao_p text, ie_tipo_guia_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ie_tiss_p text) RETURNS bigint AS $body$
DECLARE


/*

  ==========================================================================================================
||				ATENÇÃO						||
||										||
||	Esta function foi criada apenas para uso no relatório RT11 - Qtd. e Valor dos eventos por prestador		||
||										||
   =========================================================================================================

 */
/*
ie_opcao_p

T - Total, tanto importado quanto digitado;
I - Apenas improtado;
D - Apenas digitado;

*/
vl_retorno_w	double precision := 0;
qt_conta_proc_w	double precision := 0;
qt_conta_mat_w	double precision := 0;


BEGIN
	begin
	if (ie_opcao_p = 'T') then

		select	sum(coalesce(a.qt_procedimento_imp,a.qt_procedimento))
		into STRICT	qt_conta_proc_w
		from	pls_conta_proc		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	elsif (ie_opcao_p = 'I') then

		select	sum(coalesce(a.qt_procedimento_imp,0))
		into STRICT	qt_conta_proc_w
		from	pls_conta_proc		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	elsif (ie_opcao_p = 'D') then

		select	sum(coalesce(a.qt_procedimento,0))
		into STRICT	qt_conta_proc_w
		from	pls_conta_proc		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	end if;

	exception
	when others then
	qt_conta_proc_w := 0;
	end;

	begin

	if (ie_opcao_p = 'T') then

		select	sum(coalesce(a.qt_material_imp,a.qt_material))
		into STRICT	qt_conta_mat_w
		from	pls_conta_mat		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	elsif (ie_opcao_p = 'I') then

		select	sum(coalesce(a.qt_material_imp,0))
		into STRICT	qt_conta_mat_w
		from	pls_conta_mat		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	elsif (ie_opcao_p = 'D') then

		select	sum(coalesce(a.qt_material,0))
		into STRICT	qt_conta_mat_w
		from	pls_conta_mat		a,
			pls_conta		b,
			pls_protocolo_conta	c
		where	a.nr_seq_conta = b.nr_sequencia
		and	b.nr_seq_prestador_exec = nr_seq_prestador_p
		and	b.nr_seq_protocolo = c.nr_sequencia
		and	c.ie_situacao in ('A','D','T')
		and	c.ie_tipo_protocolo <> 'R'
		and	(c.dt_mes_competencia >= dt_inicio_p AND c.dt_mes_competencia <= dt_fim_p)
		and	(
			(ie_tipo_guia_p = 5 AND b.ie_tipo_guia = 6)
			or (ie_tipo_guia_p = 4 AND b.ie_tipo_guia = 5)
			or ((ie_tipo_guia_p = 2) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) <> '04'))
			or ((ie_tipo_guia_p = 1) and (b.ie_tipo_guia = 4) and (pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04'))
			or (ie_tipo_guia_p = 1 AND b.ie_tipo_guia = 3)
			)
		/*and	b.ie_tipo_guia = (
					case
						when (ie_tipo_guia_p = 5) then 6
						when (ie_tipo_guia_p = 4) then 5
						when (
							(ie_tipo_guia_p = 2)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento <> 8)
							) then 4
						when (
							(ie_tipo_guia_p = 1)
							and (b.ie_tipo_guia = 4)
							and (b.nr_seq_tipo_atendimento = 8)
							) then 4
						when (ie_tipo_guia_p = 1) then 3
						else 0
					end
				)*/
		/*and	(
				(b.ie_tipo_guia = ie_tipo_guia_p) or
						(
							(ie_tipo_guia_p = 3) and
							(b.ie_tipo_guia = 4) and
							(pls_obter_cd_tiss_atendimento(b.nr_seq_tipo_atendimento) = '04')
						)
			)*/
		--and	(((c.ie_origem_protocolo = 'E') and (nvl(ie_tiss_p,'N') = 'S')) or (nvl(ie_tiss_p,'N') = 'N'));
		and	(((b.ie_origem_conta = 'E') and (coalesce(ie_tiss_p,'N') = 'S')) or (coalesce(ie_tiss_p,'N') = 'N'));

	end if;

	exception
	when others then
	qt_conta_mat_w := 0;
	end;

	vl_retorno_w := coalesce(qt_conta_proc_w,0) + coalesce(qt_conta_mat_w,0);

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_proc_mat_conta (nr_seq_prestador_p bigint, ie_opcao_p text, ie_tipo_guia_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, ie_tiss_p text) FROM PUBLIC;

