CREATE OR REPLACE FUNCTION pls_obter_qt_prop_benef ( nr_seq_proposta_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_vidas_propostas_w	bigint := 0;


BEGIN

if (nr_seq_proposta_p IS NOT NULL AND nr_seq_proposta_p::text <> '') then

	if (ie_opcao_p = 'A') then
		select	count(*)
		into STRICT	qt_vidas_propostas_w
		from	pls_proposta_beneficiario
		where	nr_seq_proposta	= nr_seq_proposta_p
		and	coalesce(dt_cancelamento::text, '') = '';

		elsif (ie_opcao_p = 'T') then
		select	count(*)
		into STRICT	qt_vidas_propostas_w
		from	pls_proposta_beneficiario
		where	nr_seq_proposta	= nr_seq_proposta_p;

	end if;
end if;

return	qt_vidas_propostas_w;

end;
$body$
LANGUAGE PLPGSQL
