qt_protocolos_w		integer;


BEGIN

if (nr_id_transacao_p IS NOT NULL AND nr_id_transacao_p::text <> '') then

select count(1)
into STRICT qt_protocolos_w
from (
SELECT a.nr_seq_protocolo
from pls_conta a,
pls_conta_pos_estabelecido b
where a.nr_sequencia = b.nr_seq_conta
and b.nr_seq_analise in ( select c.nr_sequencia from pls_analise_conta_temp c where nr_id_transacao = nr_id_transacao_p)

union

select a.nr_seq_protocolo
from pls_conta a
where a.nr_seq_analise in (select d.nr_sequencia from pls_analise_conta_temp d where nr_id_transacao = nr_id_transacao_p)
) alias4;

end if;

return qt_protocolos_w;

end;
