CREATE OR REPLACE FUNCTION pls_obter_qt_reg_atuarial ( dt_referencia_p timestamp, ie_tipo_registro_p bigint) RETURNS bigint AS $body$
DECLARE

			 
qt_registros_w			bigint;


BEGIN 
 
if (ie_tipo_registro_p = 1) then 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	PLS_ATUARIAL_v 
	where	ie_tipo_registro	= 1;
else 
	select	count(1) 
	into STRICT	qt_registros_w 
	from	PLS_ATUARIAL 
	where	trunc(dt_referencia,'Month') = trunc(dt_referencia_p,'Month') 
	and	ie_tipo_registro	= ie_tipo_registro_p;
end if;
 
return	qt_registros_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
