SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_repasse_contrato ( nr_seq_contrato_p bigint, cd_cooperativa_p text, dt_repasse_p timestamp, ie_tipo_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*
ie_tipo_opcao_p
I - Inclusões
E - Exclusões
*/
qt_retorno_w		bigint;


BEGIN

if (ie_tipo_opcao_p	= 'I') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pls_congenere		e,
		pls_segurado_repasse	d,
		pls_segurado_carteira	c,
		pls_segurado		b,
		pls_contrato		a
	where	d.nr_seq_congenere	= e.nr_sequencia
	and	d.nr_seq_segurado	= b.nr_sequencia
	and	c.nr_seq_segurado	= b.nr_sequencia
	and	b.nr_seq_contrato	= a.nr_sequencia
	and	a.nr_sequencia		= nr_seq_contrato_p
	and	((e.cd_cooperativa	= cd_cooperativa_p) or (cd_cooperativa_p = '0'))
	and	trunc(d.dt_repasse,'Month')	= trunc(dt_repasse_p,'Month')
	and 	((coalesce(d.dt_fim_repasse::text, '') = '') or (d.dt_fim_repasse > dt_repasse_p));
elsif (ie_tipo_opcao_p	= 'E') then
	select	count(*)
	into STRICT	qt_retorno_w
	from	pls_congenere		e,
		pls_segurado_repasse	d,
		pls_segurado_carteira	c,
		pls_segurado		b,
		pls_contrato		a
	where	d.nr_seq_congenere	= e.nr_sequencia
	and	d.nr_seq_segurado	= b.nr_sequencia
	and	c.nr_seq_segurado	= b.nr_sequencia
	and	b.nr_seq_contrato	= a.nr_sequencia
	and	a.nr_sequencia		= nr_seq_contrato_p
	and	((e.cd_cooperativa	= cd_cooperativa_p) or (cd_cooperativa_p = '0'))
	and	trunc(d.dt_fim_repasse,'Month') =  trunc(dt_repasse_p,'Month')
	and 	(d.dt_fim_repasse IS NOT NULL AND d.dt_fim_repasse::text <> '');
end if;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
