CREATE OR REPLACE FUNCTION pls_obter_qt_res_comer_origem ( ie_dimensao_p text, ie_origem_solicitacao_p text, ie_opcao_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
	D = Quantidade de registros (No Delphi)
	T = Soma dos registros (No Delphi)
	DJ = Quantidade de registros (No Java)
	TJ = Soma de registros (No Java)*/
qt_retorno_w		bigint;
ie_origem_solicitacao_w	varchar(10);


BEGIN

if (ie_opcao_p in ('D','T')) then
	ie_origem_solicitacao_w	:= substr(ie_origem_solicitacao_p,2,10);
elsif (ie_opcao_p in ('DJ','TJ')) then
	ie_origem_solicitacao_w	:= substr(ie_origem_solicitacao_p,1,10);
end if;

if (ie_opcao_p in ('D','DJ')) then
	select	qt_registros
	into STRICT	qt_retorno_w
	from	w_pls_resumo_comercial
	where	ie_origem_solicitacao	= ie_origem_solicitacao_w
	and	ie_dimensao	= ie_dimensao_p;
elsif (ie_opcao_p in ('T','TJ')) then
	select	sum(qt_registros)
	into STRICT	qt_retorno_w
	from	w_pls_resumo_comercial
	where	ie_origem_solicitacao	= ie_origem_solicitacao_w;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
