SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_solic_real_item ( nr_seq_auditoria_p bigint, nr_seq_aud_item_p bigint) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Retornar a quantidade solicitada do item.
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta:
[  ]  Objetos do dicionario [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_proc_origem_w	bigint;
nr_seq_mat_origem_w	bigint;
nr_seq_guia_w		bigint;
nr_seq_requisicao_w	bigint;
nr_seq_execucao_w	bigint;
qt_retorno_w		pls_guia_plano_proc.qt_solicitada%type;
qt_ajuste_w			pls_auditoria_item.qt_ajuste%type;


BEGIN

begin
	select	nr_seq_proc_origem,
		nr_seq_mat_origem,
		qt_ajuste
	into STRICT	nr_seq_proc_origem_w,
		nr_seq_mat_origem_w,
		qt_ajuste_w
	from	pls_auditoria_item
	where	nr_sequencia	= nr_seq_aud_item_p;
exception
when others then
	nr_seq_proc_origem_w	:= null;
	nr_seq_mat_origem_w	:= null;
end;

begin
	select	nr_seq_guia,
		nr_seq_requisicao,
    nr_seq_execucao
	into STRICT	nr_seq_guia_w,
		nr_seq_requisicao_w,
    nr_seq_execucao_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_guia_w		:= null;
	nr_seq_requisicao_w	:= null;
end;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') then
	if (nr_seq_proc_origem_w IS NOT NULL AND nr_seq_proc_origem_w::text <> '') then
		begin
			select	qt_solicitada
			into STRICT	qt_retorno_w
			from	pls_guia_plano_proc
			where	nr_sequencia	= nr_seq_proc_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	elsif (nr_seq_mat_origem_w IS NOT NULL AND nr_seq_mat_origem_w::text <> '') then
		begin
			select	qt_solicitada
			into STRICT	qt_retorno_w
			from	pls_guia_plano_mat
			where	nr_sequencia	= nr_seq_mat_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	else
		qt_retorno_w	:= qt_ajuste_w;
	end if;
elsif (nr_seq_requisicao_w IS NOT NULL AND nr_seq_requisicao_w::text <> '') then
	if (nr_seq_proc_origem_w IS NOT NULL AND nr_seq_proc_origem_w::text <> '') then
		begin
			select	qt_solicitado
			into STRICT	qt_retorno_w
			from	pls_requisicao_proc
			where	nr_sequencia	= nr_seq_proc_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	elsif (nr_seq_mat_origem_w IS NOT NULL AND nr_seq_mat_origem_w::text <> '') then
		begin
			select	qt_solicitado
			into STRICT	qt_retorno_w
			from	pls_requisicao_mat
			where	nr_sequencia	= nr_seq_mat_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	else
		qt_retorno_w	:= qt_ajuste_w;
	end if;
elsif (nr_seq_execucao_w IS NOT NULL AND nr_seq_execucao_w::text <> '') then
  if (nr_seq_proc_origem_w IS NOT NULL AND nr_seq_proc_origem_w::text <> '') then
		begin
			select	qt_item
			into STRICT	qt_retorno_w
			from	pls_execucao_req_item
			where	nr_seq_req_proc	= nr_seq_proc_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	elsif (nr_seq_mat_origem_w IS NOT NULL AND nr_seq_mat_origem_w::text <> '') then
		begin
			select	qt_item
			into STRICT	qt_retorno_w
			from	pls_execucao_req_item
			where	nr_seq_req_mat	= nr_seq_mat_origem_w;
		exception
		when others then
			qt_retorno_w	:= qt_ajuste_w;
		end;
	else
		qt_retorno_w	:= qt_ajuste_w;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_solic_real_item ( nr_seq_auditoria_p bigint, nr_seq_aud_item_p bigint) FROM PUBLIC;

