CREATE OR REPLACE FUNCTION pls_obter_qt_titulares_ativos ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_intercambio_p pls_intercambio.nr_sequencia%type, nr_seq_grupo_contrato_p pls_grupo_contrato.nr_sequencia%type, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_titulares_w	bigint	:= 0;


BEGIN

if (nr_seq_grupo_contrato_p IS NOT NULL AND nr_seq_grupo_contrato_p::text <> '') then
	select	sum(qt)
	into STRICT	qt_titulares_w
	from	(SELECT	count(1) qt
		from	pls_segurado a,
			pls_contrato_grupo b
		where	a.nr_seq_contrato	= b.nr_seq_contrato
		and	b.nr_seq_grupo		= nr_seq_grupo_contrato_p
		and	coalesce(a.nr_seq_titular::text, '') = ''
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	a.dt_contratacao <= dt_referencia_p
		and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > dt_referencia_p))
		
union all

		SELECT	count(1) qt
		from	pls_segurado a,
			pls_contrato_grupo b
		where	a.nr_seq_intercambio	= b.nr_seq_intercambio
		and	b.nr_seq_grupo		= nr_seq_grupo_contrato_p
		and	coalesce(a.nr_seq_titular::text, '') = ''
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	a.dt_contratacao <= dt_referencia_p
		and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > dt_referencia_p))) alias16;
elsif (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then
	select	count(1)
	into STRICT	qt_titulares_w
	from	pls_segurado
	where	nr_seq_contrato	= nr_seq_contrato_p
	and	coalesce(nr_seq_titular::text, '') = ''
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	dt_contratacao <= dt_referencia_p
	and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > dt_referencia_p));
elsif (nr_seq_intercambio_p IS NOT NULL AND nr_seq_intercambio_p::text <> '') then
	select	count(1)
	into STRICT	qt_titulares_w
	from	pls_segurado
	where	nr_seq_intercambio	= nr_seq_intercambio_p
	and	coalesce(nr_seq_titular::text, '') = ''
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	dt_contratacao <= dt_referencia_p
	and	((coalesce(dt_rescisao::text, '') = '') or (dt_rescisao > dt_referencia_p));

end if;

return	coalesce(qt_titulares_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
