SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_vidas_bonif_prop ( nr_seq_proposta_p bigint, nr_seq_bonificacao_p bigint, IE_POSSUI_BONIFICACAO_p text) RETURNS bigint AS $body$
DECLARE


qt_vidas_contrato_w	bigint := 0;
qt_vidas_propostas_w	bigint := 0;
nr_contrato_w		bigint;
nr_seq_contrato_w	bigint;
ie_tipo_proposta_w	varchar(10);
qt_registros_w		bigint;


BEGIN

if (IE_POSSUI_BONIFICACAO_p = 'N') then
	return pls_obter_qt_vidas_proposta(nr_seq_proposta_p,null);
else
	select	nr_seq_contrato,
		ie_tipo_proposta
	into STRICT	nr_contrato_w,
		ie_tipo_proposta_w
	from	pls_proposta_adesao
	where	nr_sequencia = nr_seq_proposta_p;

	if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') and (ie_tipo_proposta_w <> 9) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_contrato_w
		from	pls_contrato
		where	nr_contrato = nr_contrato_w;

		select	count(1)
		into STRICT	qt_registros_w
		from	pls_bonificacao_vinculo
		where	nr_seq_contrato		= nr_seq_contrato_w
		and	nr_seq_bonificacao	= nr_seq_bonificacao_p;

		if (qt_registros_w = 0) then
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			from	pls_segurado		b,
				pls_bonificacao_vinculo	a
			where	a.nr_seq_segurado	= b.nr_sequencia
			and	b.nr_seq_contrato	= nr_seq_contrato_w
			and	a.nr_seq_bonificacao	= nr_seq_bonificacao_p
			and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
			and	coalesce(b.dt_rescisao::text, '') = '';
		else
			select	count(*)
			into STRICT	qt_vidas_contrato_w
			from	pls_segurado
			where	nr_seq_contrato	= nr_seq_contrato_w
			and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
			and	coalesce(dt_rescisao::text, '') = '';
		end if;
	end if;

	select	count(1)
	into STRICT	qt_registros_w
	from	pls_bonificacao_vinculo
	where	nr_seq_proposta	= nr_seq_proposta_p
	and	nr_seq_bonificacao	= nr_seq_bonificacao_p;

	if (qt_registros_w = 0) then
		select	count(*)
		into STRICT	qt_vidas_propostas_w
		from	pls_proposta_beneficiario	b,
			pls_bonificacao_vinculo		a
		where	a.NR_SEQ_SEGURADO_PROP	= b.nr_sequencia
		and	b.nr_seq_proposta	= nr_seq_proposta_p
		and	a.nr_seq_bonificacao	= nr_seq_bonificacao_p
		and	coalesce(b.dt_cancelamento::text, '') = '';
	else
		select	count(*)
		into STRICT	qt_vidas_propostas_w
		from	pls_proposta_beneficiario
		where	nr_seq_proposta	= nr_seq_proposta_p
		and	coalesce(dt_cancelamento::text, '') = '';
	end if;

	return qt_vidas_propostas_w + qt_vidas_contrato_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_vidas_bonif_prop ( nr_seq_proposta_p bigint, nr_seq_bonificacao_p bigint, IE_POSSUI_BONIFICACAO_p text) FROM PUBLIC;

