CREATE OR REPLACE FUNCTION pls_obter_qt_vidas_grupo_relac ( nr_seq_grupo_p bigint, dt_base_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_vidas_w	bigint;


BEGIN

select	sum(qt_beneficiarios)
into STRICT	qt_vidas_w
from	(	SELECT	sum(1) qt_beneficiarios
		from	pls_segurado		a,
			pls_contrato		b,
			pls_contrato_grupo	d,
			pls_grupo_contrato	c
		where	a.nr_seq_contrato	= b.nr_sequencia
		and	d.nr_seq_contrato	= b.nr_sequencia
		and	d.nr_seq_grupo		= c.nr_sequencia
		and	c.nr_sequencia		= nr_seq_grupo_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > dt_base_p and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> '')))
		and	a.dt_contratacao <= fim_dia(last_day(dt_base_p))
		
union all

		SELECT	sum(1) qt_beneficiarios
		from	pls_segurado		a,
			pls_intercambio		b,
			pls_contrato_grupo	d,
			pls_grupo_contrato	c
		where	a.nr_seq_intercambio	= b.nr_sequencia
		and	d.nr_seq_intercambio	= b.nr_sequencia
		and	d.nr_seq_grupo		= c.nr_sequencia
		and	c.nr_sequencia		= nr_seq_grupo_p
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
		and	((coalesce(a.dt_rescisao::text, '') = '') or (a.dt_rescisao > dt_base_p and (a.dt_rescisao IS NOT NULL AND a.dt_rescisao::text <> '')))
		and	a.dt_contratacao <= fim_dia(last_day(dt_base_p))) alias19;

return	coalesce(qt_vidas_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
