SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_qt_vidas_proposta ( nr_seq_proposta_p bigint, nr_seq_proposta_benef_p bigint) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			bigint := 0;
qt_vidas_contrato_w		bigint := 0;
qt_vidas_propostas_w		bigint := 0;
nr_contrato_w			bigint;
nr_seq_contrato_w		bigint;
ie_tipo_proposta_w		varchar(10);
nr_seq_tabela_benef_w		pls_tabela_preco.nr_sequencia%type;
nr_seq_titular_w		pls_proposta_beneficiario.nr_seq_titular%type;
ie_preco_vidas_contrato_w	pls_tabela_preco.ie_preco_vidas_contrato%type;
ie_calculo_vidas_w		pls_tabela_preco.ie_calculo_vidas%type;
qt_vidas_w			bigint := 0;


BEGIN

if (coalesce(nr_seq_proposta_benef_p::text, '') = '' and (nr_seq_proposta_p IS NOT NULL AND nr_seq_proposta_p::text <> '')) then
	select	nr_seq_contrato,
		ie_tipo_proposta
	into STRICT	nr_contrato_w,
		ie_tipo_proposta_w
	from	pls_proposta_adesao
	where	nr_sequencia = nr_seq_proposta_p;

	if (nr_contrato_w IS NOT NULL AND nr_contrato_w::text <> '') and (ie_tipo_proposta_w <> 9) then
		select	max(nr_sequencia)
		into STRICT	nr_seq_contrato_w
		from	pls_contrato
		where	nr_contrato = nr_contrato_w;

		select	count(*)
		into STRICT	qt_vidas_contrato_w
		from	pls_segurado
		where	nr_seq_contrato	= nr_seq_contrato_w
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	coalesce(dt_rescisao::text, '') = '';
	end if;

	select	count(*)
	into STRICT	qt_vidas_propostas_w
	from	pls_proposta_beneficiario
	where	nr_seq_proposta	= nr_seq_proposta_p
	and	coalesce(dt_cancelamento::text, '') = '';

qt_retorno_w	:= qt_vidas_propostas_w + qt_vidas_contrato_w;

elsif (nr_seq_proposta_benef_p IS NOT NULL AND nr_seq_proposta_benef_p::text <> '') then

	select	nr_seq_tabela,
		nr_seq_titular
	into STRICT	nr_seq_tabela_benef_w,
		nr_seq_titular_w
	from	pls_proposta_beneficiario
	where	nr_sequencia 	= nr_seq_proposta_benef_p
	and	coalesce(dt_cancelamento::text, '') = '';

	if (nr_seq_tabela_benef_w IS NOT NULL AND nr_seq_tabela_benef_w::text <> '') then

		select	coalesce(ie_preco_vidas_contrato,'N'),
			coalesce(ie_calculo_vidas,'A')
		into STRICT	ie_preco_vidas_contrato_w,
			ie_calculo_vidas_w
		from	pls_tabela_preco
		where	nr_sequencia	= nr_seq_tabela_benef_w;


		if (ie_preco_vidas_contrato_w = 'S') then
			if (ie_calculo_vidas_w = 'A') then
				select	count(1)
				into STRICT	qt_vidas_w
				from	pls_proposta_beneficiario
				where   nr_seq_proposta = nr_seq_proposta_p
				and	coalesce(dt_cancelamento::text, '') = '';

			elsif (ie_calculo_vidas_w = 'T') then
				select	count(1)
				into STRICT	qt_vidas_w
				from	pls_proposta_beneficiario
				where	nr_seq_proposta = nr_seq_proposta_p
				and	coalesce(nr_seq_titular::text, '') = ''
				and	coalesce(dt_cancelamento::text, '') = '';
			elsif (ie_calculo_vidas_w = 'D') then
				select	count(1)
				into STRICT	qt_vidas_w
				from	pls_proposta_beneficiario
				where	nr_seq_proposta = nr_seq_proposta_p
				and	(nr_seq_titular IS NOT NULL AND nr_seq_titular::text <> '')
				and	coalesce(dt_cancelamento::text, '') = '';
			elsif (ie_calculo_vidas_w = 'TD') then
				select	count(1)
				into STRICT	qt_vidas_w
				from	pls_proposta_beneficiario a
				where	a.nr_seq_proposta = nr_seq_proposta_p
				and	coalesce(a.dt_cancelamento::text, '') = ''
				and	((coalesce(a.nr_seq_titular::text, '') = '') or ((a.nr_seq_titular IS NOT NULL AND a.nr_seq_titular::text <> '') and ((	SELECT	count(1)
														from	grau_parentesco x
														where	x.nr_sequencia = a.nr_seq_parentesco
														and	x.ie_tipo_parentesco = '1') > 0)));
			elsif (ie_calculo_vidas_w = 'F') then
				qt_vidas_w	:= coalesce(pls_obter_qt_familia_proposta(nr_seq_proposta_benef_p,nr_seq_titular_w),0);
			end if;
		else
			qt_vidas_w	:= 0;
		end if;

		qt_retorno_w	:= qt_vidas_w;
	end if;
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_qt_vidas_proposta ( nr_seq_proposta_p bigint, nr_seq_proposta_benef_p bigint) FROM PUBLIC;

